// import libaries
import React, { Component } from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  withRouter
} from "react-router-dom";
import firebase from './Firebase';

// import components
import Home from './components/Home';
import Welcome from './components/Welcome';
import Login from './components/Login';
import Register from './components/Register';
import Meetings from './components/Meetings';
import Navigation from './components/Navigation';



class App extends Component {

  constructor() {
    super();
    this.state = {
      user: null,
      displayName: null,
      userID: null
    };

  }

  componentDidMount() {
    firebase
    .auth()
    .onAuthStateChanged(FBUser => {
      if (FBUser) {
        this.setState({
          user: FBUser,
          displayName: FBUser.displayName,
          userID: FBUser.uid
        });

        const meetingsRef = firebase
        .database()
        .ref('meetings/' + FBUser.uid);

        meetingsRef.on('value', snapshot => {

          let meetings = snapshot.val();
          let meetingsList = [];

          for(let item in meetings){
            meetingsList.push({
              meetingId: item,
              meetingName: meetings[item].meetingName
            });
          }

          this.setState({
            meetings: meetingsList,
            howManyMeetings: meetingsList.length
          });

        })

      } else {
        this.setState({ user: null });
      }
    });
  }

  // register user
  registerUser = userName => {
    firebase
    .auth()
    .onAuthStateChanged(FBUser => {
      FBUser.updateProfile({
        displayName: userName
      }).then(() => {
        this.setState({
          user: FBUser,
          displayName: FBUser.displayName,
          userID: FBUser.uid
        });

        // redirect to meetings route
        this.props.history.push('/meetings');
      })
    })
  };


  // logout user 
  logOutUser = e => {
    e.preventDefault();
    this.setState({
      user: null,
      displayName: null,
      userID: null
    });

    firebase
    .auth()
    .signOut()
    .then(() => {
      // redirect to meetings route
      this.props.history.push('/login');
    })
  };

  // add meeting
  addMeeting = meetingName => {
    const ref = firebase
    .database()
    .ref(`meetings/${this.state.user.uid}`);
    ref.push({ meetingName: meetingName });
  };

  render() {
    return (
      <div>
        <Navigation user={this.state.user} logOutUser={this.logOutUser} /> 
        {this.state.user && <Welcome userName={this.state.displayName} logOutUser={this.logOutUser}/> }
          <Switch>
            <Route path="/login">
                <Login /> 
            </Route>  
            <Route path="/register">
                <Register registerUser={this.registerUser} /> 
            </Route> 
            <Route path="/meetings">
                <Meetings addMeeting={this.addMeeting} meetings={this.state.meetings}/> 
            </Route> 
            <Route path="/">
                <Home user={this.state.user} /> 
            </Route>  
          </Switch>
      </div>
    );
  }
}

export default withRouter(App);
