// import libaries
import React, { Component } from 'react';
import { FaUndo, FaRandom } from 'react-icons/fa';

// import components
import firebase from '../Firebase';
import AttendeesList from './AttendeesList';

class Attendees extends Component {

    constructor(props) {
        super(props);
        this.state = {
            allAttendees: [],
            searchQuery: '',
            displayAttendees: []
        };

        this.handleChange = this.handleChange.bind(this);
        this.resetQuery = this.resetQuery.bind(this);
        this.chooseRandom = this.chooseRandom.bind(this);
    }


    componentDidMount() {
        const ref = firebase
        .database()
        .ref(`meetings/${this.props.userID}/${this.props.meetingID}/attendees`);

        ref.on('value', snapshot => {
            let attendees = snapshot.val();
            let attendeesList = [];

            for(let item in attendees){
                attendeesList.push({
                    attendeeID: item,
                    attendeeName: attendees[item].attendeeName,
                    attendeeEmail: attendees[item].attendeeEmail,
                    star: attendees[item].star
                });
            }

            this.setState({
                displayAttendees: attendeesList,
                allAttendees: attendeesList
            });
        });
    }

    // handle change in input fields 
    handleChange(e) {
        const itemName = e.target.name;
        const itemValue = e.target.value;

        this.setState({ [itemName]: itemValue });
    };

    // choose random attendee
    chooseRandom(){
        const randomAttendee = Math.floor(Math.random()*this.state.allAttendees.length);
        this.resetQuery();
        this.setState({
            displayAttendees: [this.state.allAttendees[randomAttendee]]
        });
    }

    // reset search query
    resetQuery(){
        this.setState({
            searchQuery: '',
            displayAttendees: this.state.allAttendees
        });
    }


    render(){

        // search filter 
        const dataFilter = item => 
          item.attendeeName
            .toLowerCase()
            .match(this.state.searchQuery.toLowerCase()) && true;

            const filteredAttendees = this.state.displayAttendees.filter(dataFilter);

        return (
          <div className="container mt-4">
            <div className="row justify-content-center">
              <div className="col-md-8">
                <h1 className="font-weight-light text-center">
                  Attendees
                </h1>
                <div className="card bg-light mb-4">
                    <div className="card-body text-center">
                        <div className="input-group input-group-lg">
                          <input 
                            type="text" 
                            name="searchQuery" 
                            value={this.state.searchQuery} 
                            placeholder="Search Attendees" 
                            className="form-control" 
                            onChange={this.handleChange}
                            />
                            <div className="input-group-append">
                              <button 
                                className="btn btn-sm btn-outline-info"
                                title="Pick a random attendee"
                                onClick={() => this.chooseRandom()}
                                >
                                  <FaRandom /> 
                              </button>
                              <button 
                                className="btn btn-sm btn-outline-info"
                                title="Reset Search"
                                onClick={() => this.resetQuery()}
                                >
                                  <FaUndo /> 
                              </button>
                            </div>
                        </div> 
                    </div>
                </div>

              </div>
            </div>
            <AttendeesList 
              userID={this.props.userID} 
              meetingID={this.props.meetingID}
              attendees={filteredAttendees}
              adminUser={this.props.adminUser}
            /> 
          </div>
        );
    }
}

export default Attendees;
